import React ,{useState} from "react";
import './Form.css'
import Error from "./Error";
const Form=(props)=>
{  const [userName,SetName]=useState('');
  const [userAge,SetAge]=useState('');
  const[error,SetError]=useState()
  
  const userNameHandler=(e)=>{
    e.preventDefault();
    SetName(e.target.value);
  }

  const userAgeHandler=(e)=>{
  
    e.preventDefault();
  SetAge(e.target.value);
  }

     const AddUserHandler=(e)=>{
    
        e.preventDefault();

        if(userName.trim().length===0 || userAge.trim().length===0)
        {
          SetError({
            title:'Invalid Input',
            message:"Please Enter valid name and age(>0)"
          })
        }
        else if(+userAge<1)
        { SetError({
          title:'Invalid age',
          message:"Please Enter valid name and age(>0)"
        })

        }
        else{
        const obj={
            name:userName,
            age:userAge
        }
        props.getdata(obj)
      }
     
        
                 


}
const ErrorHandler=()=>{
  SetError(null);
}


 return <div>
<form className="form-control" onSubmit={AddUserHandler}>
    <label >UserName</label>
    <br></br>
    <input type="text" value={userName} onChange={userNameHandler}></input>
    <br/>
    <label>age</label>
    <br></br>
    <input type="number" value={userAge}  onChange={userAgeHandler}></input>
    <br></br>
    <button type="submit">Add User</button>
</form>
{ error && <Error error={error} seterror={ErrorHandler}> </Error>

}

    </div>


}
export default Form;
